#import "AWPokerMgr.h"
#import "AWSettingsMgr.h"
@interface AWPokerMgr()
+(NSDictionary*)heroDic;
+(NSDictionary*)magicDic;
+(NSDictionary*)weaponDic;
+(NSDictionary*)heroTWDic;
+(NSDictionary*)magicTWDic;
+(NSDictionary*)weaponTWDic;

+(NSDictionary*)magicDetailCNDic;
+(NSDictionary*)heroDetailCNDic;
+(NSDictionary*)weaponDetailCNDic;

@end

@implementation AWPokerMgr
+(NSDictionary*)getPokerDic:(TPokerType)type
{
    NSUserDefaults* defs = [NSUserDefaults standardUserDefaults];
    NSArray* languages = [defs objectForKey:@"AppleLanguages"];
    NSString* preferredLang = [languages objectAtIndex:0];
    if ([preferredLang isEqualToString:@"zh-Hant"]) {
        switch (type) {
            case EPokerTypeHero:
                return [AWPokerMgr heroTWDic];
            case EPokerTypeMagic:
                return [AWPokerMgr magicTWDic];
            case EPokerTypeWeapon:
                return [AWPokerMgr weaponTWDic];
                
            default:
                break;
        }
    }
    else {
        switch (type) {
            case EPokerTypeHero:
                return [AWPokerMgr heroDic];
            case EPokerTypeMagic:
                return [AWPokerMgr magicDic];
            case EPokerTypeWeapon:
                return [AWPokerMgr weaponDic];
                
            default:
                break;
        }
    }
    return nil;
}

+(NSDictionary*)getDetailDic:(TPokerType)type
{
    NSUserDefaults* defs = [NSUserDefaults standardUserDefaults];
    NSArray* languages = [defs objectForKey:@"AppleLanguages"];
    NSString* preferredLang = [languages objectAtIndex:0];
    if ([preferredLang isEqualToString:@"zh-Hant"]) {
        switch (type) {
            case EPokerTypeHero:
                return [AWPokerMgr heroTWDic];
            case EPokerTypeMagic:
                return [AWPokerMgr magicTWDic];
            case EPokerTypeWeapon:
                return [AWPokerMgr weaponTWDic];
            default:
                break;
        }
    }
    else {
        switch (type) {
            case EPokerTypeHero:
                return [AWPokerMgr heroDetailCNDic];
            case EPokerTypeMagic:
                return [AWPokerMgr magicDetailCNDic];
            case EPokerTypeWeapon:
                return [AWPokerMgr weaponDetailCNDic];
                
            default:
                break;
        }
    }
    return nil;
}

